  // const [title, setTitle] = useState("");
  // const bodyInputRef = useRef();
  // const [description, setDescription] = useState("");
  const [post, setPost] = useState({ title: "", body: "" });

  const addNewPost = (e) => {
    e.preventDefault();
    // const newPost = {
    //   id: Date.now(),
    //   title,
    //   description,
    // };
    // console.log(newPost);
