import React, { useRef, useState } from "react";
import "./styles/App.css";
import PostList from "./components/PostList";
import MyButton from "./components/UI/button/MyButton";
import MyInput from "./components/UI/input/MyInput";

const App = () => {
  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "JavaScript",
      description: "JavaScript is programming language",
    },
    {
      id: 2,
      title: "C#",
      description: "C# is programming language",
    },
    {
      id: 3,
      title: "Java",
      description: "Java is programming language",
    },
  ]);

  // const [title, setTitle] = useState("");
  // const bodyInputRef = useRef();
  // const [description, setDescription] = useState("");
  const [post, setPost] = useState({ title: "", body: "" });

  const addNewPost = (e) => {
    e.preventDefault();
    // const newPost = {
    //   id: Date.now(),
    //   title,
    //   description,
    // };
    // console.log(newPost);
    setPosts([...posts, newPost]);
    // setTitle("");
    // setDescription("");
    // console.log(bodyInputRef.current.value);
    setPost({ title: "", body: "" });
  };

  return (
    <div className="App">
      <form action="">
        {/* controlled element */}
        <MyInput
          value={post.title}
          // onChange={(e) => setTitle(e.target.value)}
          onChange={(e) => setPost({ ...post, title: e.target.value })}
          type="text"
          placeholdet="Title of the post"
        />
        <MyInput
          value={post.description}
          // onChange={(e) => setDescription(e.target.value)}
          onChange={(e) => setPost({ ...post, description: e.target.value })}
          type="text"
          placeholdet="Description of the post"
        />
        {/* uncontrolled element
        <MyInput
          ref={bodyInputRef}
          type="text"
          placeholdet="Description of the post"
        /> */}
        <MyButton onClick={addNewPost}>Create post</MyButton>
      </form>
      <PostList posts={posts} title="List" />
    </div>
  );
};

export default App;
